define({"topics" : [{"title":"Sample Use Cases","href":"datacollector\/UserGuide\/Destinations\/Snowflake.html#concept_epr_tsh_ggb","attributes": {"data-id":"concept_epr_tsh_ggb",},"menu": {"hasChildren":false,},"tocID":"concept_epr_tsh_ggb-d46e129457","topics":[]},{"title":"Prerequisites","href":"datacollector\/UserGuide\/Destinations\/Snowflake.html#concept_ysy_fcj_ggb","attributes": {"data-id":"concept_ysy_fcj_ggb",},"menu": {"hasChildren":true,},"tocID":"concept_ysy_fcj_ggb-d46e129479","next":"concept_ysy_fcj_ggb-d46e129479",},{"title":"Load Methods","href":"datacollector\/UserGuide\/Destinations\/Snowflake.html#concept_w35_vsq_2gb","attributes": {"data-id":"concept_w35_vsq_2gb",},"menu": {"hasChildren":false,},"tocID":"concept_w35_vsq_2gb-d46e130028","topics":[]},{"title":"Performance Optimization","href":"datacollector\/UserGuide\/Destinations\/Snowflake.html#concept_mzv_wjw_2gb","attributes": {"data-id":"concept_mzv_wjw_2gb",},"menu": {"hasChildren":false,},"tocID":"concept_mzv_wjw_2gb-d46e130141","topics":[]},{"title":"Row Generation","shortdesc":"\n               <p class=\"shortdesc\">When writing a record to a table, the Snowflake destination includes all record         fields in the resulting row, by default.\n                  The destination uses the root field,             <code class=\"ph codeph\">\/</code>, as the basis for the resulting row.\n               </p>\n            ","href":"datacollector\/UserGuide\/Destinations\/Snowflake.html#concept_c3g_qfj_ggb","attributes": {"data-id":"concept_c3g_qfj_ggb",},"menu": {"hasChildren":false,},"tocID":"concept_c3g_qfj_ggb-d46e130264","topics":[]},{"title":"Writing to Multiple Tables","shortdesc":"\n               <p class=\"shortdesc\">You can use the Snowflake destination to write to multiple tables within a Snowflake         schema. To write to multiple\n                  tables, you specify a field in the record that specifies the         table to write to.\n               </p>\n            ","href":"datacollector\/UserGuide\/Destinations\/Snowflake.html#concept_w15_crp_2gb","attributes": {"data-id":"concept_w15_crp_2gb",},"menu": {"hasChildren":false,},"tocID":"concept_w15_crp_2gb-d46e130403","topics":[]},{"title":"Creating Columns and Tables for Data Drift","shortdesc":"\n               <p class=\"shortdesc\">You can configure the Snowflake destination to automatically compensate for changes         in column or table requirements,\n                  also known as data drift.\n               </p>\n            ","href":"datacollector\/UserGuide\/Destinations\/Snowflake.html#concept_cn1_msp_2gb","attributes": {"data-id":"concept_cn1_msp_2gb",},"menu": {"hasChildren":true,},"tocID":"concept_cn1_msp_2gb-d46e130549","next":"concept_cn1_msp_2gb-d46e130549",},{"title":"Define the CRUD Operation","href":"datacollector\/UserGuide\/Destinations\/Snowflake.html#concept_umx_qgj_ggb","attributes": {"data-id":"concept_umx_qgj_ggb",},"menu": {"hasChildren":false,},"tocID":"concept_umx_qgj_ggb-d46e130871","topics":[]},{"title":"Configuring a Snowflake Destination","href":"datacollector\/UserGuide\/Destinations\/Snowflake.html#task_nfs_c2k_mfb","attributes": {"data-id":"task_nfs_c2k_mfb",},"menu": {"hasChildren":false,},"tocID":"task_nfs_c2k_mfb-d46e131044","topics":[]}]});